var returnUsersFromClass = Class.create();
returnUsersFromClass.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    returnUsers: function() {
        var incidents = [];
        var user = new GlideRecord('sys_user');

        var inc = new GlideRecord('x_58872_needit_needit');
        inc.addQuery('opened_by', gs.getUserID());
        inc.query();

        while (inc.next()) {
            incidents.push(inc.number.toString());
        }
        gs.info('numbers ' + incidents);
        return incidents;
    },
    type: 'returnUsersFromClass'
});
