sys_ui_page.client_script:
function confirm(needs, answer) {
	var ga = new GlideAjax('updateNeeds');
	ga.addParam('sysparm_name','getNeeds');
	ga.addParam('sysparm_needs',needs);
	ga.addParam('sysparm_answer', answer);
	ga.getXML(cb);

	function cb(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		console.log(answer);
		GlideModal.get().destroy();
		reloadWindow(window);
	}
}

function cancel() {
	GlideModal.get().destroy();
}
